// src/components/AccordionMenu.js
import React, { useState } from 'react';

const AccordionMenu = () => {
  const [openMonth, setOpenMonth] = useState(null); // State to track the open month

  const handleToggle = (month) => {
    // Toggle the month section
    setOpenMonth(openMonth === month ? null : month);
  };

  const movies = {
    January: ['Upcoming Movies', 'This Week Movies'],
    February: ['Upcoming Movies', 'This Week Movies'],
  };

  return (
    <div className="accordion">
      <h2 className="text-2xl font-bold mb-4">Year 2024</h2>
      {Object.keys(movies).map((month) => (
        <div key={month}>
          <div
            className="cursor-pointer bg-gray-200 p-2 rounded"
            onClick={() => handleToggle(month)}
          >
            <h3 className="text-xl">{month}</h3>
          </div>
          {openMonth === month && (
            <div className="pl-4">
              {movies[month].map((movieType, index) => (
                <div key={index} className="pl-2">
                  {`${index + 1}. ${movieType}`}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default AccordionMenu;
