import React, { useEffect, useState } from "react";
import { getUserBookings, cancelBooking } from "../services/api";

const BookingDetails = () => {
  const [bookings, setBookings] = useState([]);

  // Load user's bookings
  useEffect(() => {
    const userId = 1; // Replace with logged-in user ID or retrieve from session
    getUserBookings(userId).then((response) => {
      // Set only active bookings (not cancelled)
      setBookings(response.data); // Initially set all bookings
    });
  }, []);

  const handleCancel = (bookingId) => {
    if (window.confirm("Do you really want to cancel this booking?")) {
      cancelBooking(bookingId)
        .then(() => {
          alert("Booking cancelled successfully");
          // Update the bookings state to mark the booking as cancelled
          setBookings((prevBookings) => 
            prevBookings.map((booking) => 
              booking.id === bookingId ? { ...booking, isCancelled: true } : booking // Mark as cancelled
            )
          );
        })
        .catch((error) => {
          console.error("Error cancelling booking:", error.response?.data || error.message);
          alert("Failed to cancel booking. Please try again.");
        });
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Your Bookings</h2>
      {bookings.length > 0 ? (
        bookings.map((booking) => (
          <div
            key={booking.id}
            className={`bg-white p-4 rounded-lg shadow-lg mb-4 ${
              booking.isCancelled ? "bg-red-100 border border-red-500" : ""
            }`} // Apply red styles if cancelled
          >
            <p className={`font-semibold ${booking.isCancelled ? "text-red-700" : ""}`}>
              Movie: {booking.movie.title}
            </p>
            <p>Booking Time: {new Date(booking.bookingTime).toLocaleString()}</p>
            {!booking.isCancelled && (
              <button
                onClick={() => handleCancel(booking.id)}
                className="btn mt-2 bg-red-500 text-white p-2 rounded"
              >
                Cancel Booking
              </button>
            )}
            {booking.isCancelled && (
              <p className="text-red-500">This booking has been cancelled.</p>
            )}
          </div>
        ))
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
};

export default BookingDetails;
