// src/components/AccordionMenu.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AccordionMenu = () => {
  const [openYear, setOpenYear] = useState(null);

  const toggleYear = (year) => {
    setOpenYear(openYear === year ? null : year);
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Movies</h2>
      {[2024].map((year) => (
        <div key={year}>
          <h3 className="text-xl font-semibold cursor-pointer" onClick={() => toggleYear(year)}>{year}</h3>
          {openYear === year && (
            <div className="ml-6">
              {['January', 'February'].map((month) => (
                <div key={month} className="my-2">
                  <h4 className="font-semibold">{month}</h4>
                  <Link to="/movies/upcoming" className="text-blue-500 hover:underline">Upcoming Movies</Link>
                  <br />
                  <Link to="/movies/this_week" className="text-blue-500 hover:underline">This Week Movies</Link>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default AccordionMenu;
