import React, { useEffect, useState } from "react";
import { getUserBookings, cancelBooking } from "../services/api";

const BookingDetails = () => {
  const [bookings, setBookings] = useState([]);

  // Load user's bookings
  useEffect(() => {
    const userId = 1; // Replace with logged-in user ID or retrieve from session
    const fetchBookings = async () => {
      try {
        const response = await getUserBookings(userId);
        // Filter out cancelled bookings right away if needed
        const activeBookings = response.data.filter((booking) => !booking.isCancelled);
        setBookings(activeBookings);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const handleCancel = (bookingId) => {
    if (window.confirm("Do you really want to cancel this booking?")) {
      cancelBooking(bookingId)
        .then(() => {
          alert("Booking cancelled successfully");
          // Immediately update the bookings state to remove the cancelled booking
          setBookings((prevBookings) =>
            prevBookings.filter((booking) => booking.id !== bookingId) // Remove cancelled booking
          );
        })
        .catch((error) => {
          console.error("Error cancelling booking:", error.response?.data || error.message);
          alert("Failed to cancel booking. Please try again.");
        });
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Your Bookings</h2>
      {bookings.length > 0 ? (
        bookings.map((booking) => (
          <div
            key={booking.id}
            className="bg-white p-4 rounded-lg shadow-lg mb-4"
          >
            <p>Movie: {booking.movie.title}</p>
            <p>Booking Time: {new Date(booking.bookingTime).toLocaleString()}</p>
            <button
              onClick={() => handleCancel(booking.id)}
              className="btn mt-2 bg-red-500 text-white p-2 rounded"
            >
              Cancel Booking
            </button>
          </div>
        ))
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
};

export default BookingDetails;
